sys_script_include.script:
var ResNet_Survey = Class.create();
ResNet_Survey.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	get_past: function(x0){
		var x = this.getParameter('sysparm_x') || x0;
		var result = '';
		var get_gr = new GlideRecord('x_uocsd_resnetwifi_submissions');
		get_gr.addEncodedQuery('submitter=' + x);
		get_gr.orderByDesc('sys_created_on');
		get_gr.setLimit(1);
		get_gr.query();
		if (get_gr.next()){
			var fields = ['question_easily_connected_score','question_wifi_performance_score','question_wifi_disconnects_score','device_comptuer_os','device_computer_model','device_computer_type','device_phone_os','device_phone_model','case_requested','case_record','comment'];
			var past = [];
			for (var i in fields){
				if (get_gr.getDisplayValue(fields[i]) != null && get_gr.getDisplayValue(fields[i]) != '') past.push(get_gr[fields[i]].getLabel() + ": " + get_gr.getDisplayValue(fields[i]));
			}
			result = past.join('\n');
		}
		return result;
	},

	type: 'ResNet_Survey'
});
